function navOnScroll(){const e=document.querySelector(".navbar"),t=document.querySelector(".navbar-brand"),o=document.querySelectorAll(".nav-link"),l=document.querySelector(".social-media"),s=document.getElementById("navbar-toggler-icon");if(document.body.scrollTop>400||document.documentElement.scrollTop>200){e.classList.add("bg-white");for(let e=0;e<o.length;e++)o[e].classList.remove("text-white");s.classList.remove("text-white"),t.classList.remove("text-white"),l.classList.remove("text-white")}else{e.classList.remove("bg-white");for(let e=0;e<o.length;e++)o[e].classList.add("text-white");s.classList.add("text-white"),t.classList.add("text-white"),l.classList.add("text-white")}e.style.transition="0.5s all ease"}window.onscroll=function(){navOnScroll()};let welcomeText=new Typed("#welcome-text",{strings:["Portfolio","Journey","Experience"],typeSpeed:80,backSpeed:80,loop:!0});window.addEventListener("load",()=>{document.querySelector(".preloader").classList.add("finish")}),AOS.init();